Suite	*input_suite(void)
{
	Suite	*s;
	TCase	*tc;

	s = suite_create("Input");
	tc = tcase_create("Input");
	tcase_add_checked_fixture(tc, data_setup, data_teardown);
	tcase_add_test(tc, init_data);
	tcase_add_test(tc, input_small);
	tcase_add_test(tc, input_int_limits);
	tcase_add_test(tc, input_big);
	tcase_add_test(tc, input_huge);
	tcase_add_test(tc, input_error_char1);
	tcase_add_test(tc, input_error_char2);
	tcase_add_test(tc, input_error_char3);
	tcase_add_test(tc, input_error_char4);
	tcase_add_test(tc, input_error_misplaced_signs1);
	tcase_add_test(tc, input_error_misplaced_signs2);
	tcase_add_test(tc, input_error_misplaced_signs3);
	tcase_add_test(tc, input_error_misplaced_signs4);
	tcase_add_test(tc, input_error_not_int1);
	tcase_add_test(tc, input_error_not_int2);
	tcase_add_test(tc, input_error_not_int3);
	tcase_add_test(tc, input_error_not_int4);
	tcase_add_test(tc, input_error_duplicates1);
	tcase_add_test(tc, input_error_duplicates2);
	tcase_add_test(tc, input_error_duplicates3);
	tcase_add_test(tc, input_error_duplicates4);
	tcase_add_test(tc, input_error_nothing);
	tcase_add_test(tc, input_error_single);
	tcase_add_test(tc, input_error_empty1);
	tcase_add_test(tc, input_error_empty2);
	tcase_add_test(tc, input_error_empty3);
	suite_add_tcase(s, tc);
	return (s);
}
